import { useEffect, useReducer } from 'react'
import Header from './Header'
import Loader from './Loader'
import Error from './Error'
import StartScreen from './StartScreen'
import Main from './Main'

const initialState={
  questions: [],
  // status could be loading, error, ready, active , finished
  status: "loading"
}

function reducer(state,action){
  switch(action.type){
    case "dataRecieved":{
      return {
        ...state,
        questions : action.payload,
        status: "ready",
      }
    }
    case "dataFailed":
      return {
        ...state, status :"error"
      }
    default:{
      throw new Error("Action Known")
    }
  }
}

export default function App(){
  const [{question, status}, dispatch] = useReducer(reducer, initialState)

  useEffect(function(){
    fetch("http://localhost:8000/questions").then(res=>res.json()).then(data=>dispatch({type:"dataRecieved", payload: data})).catch(err=>dispatch({type:"dataFailed"}))
  },[])

  return <div className="app">
    <Header />
    <Main>
      {status === "loading" && <Loader/>}
      {status === "error" && <Error/>}
      {status === "ready" && <StartScreen/>}
    </Main>
  </div>
}
