import { useEffect, useReducer } from 'react'
import Header from './components/Header'
import Loader from './components/Loader'
import Error from './components/Error'
import StartScreen from './components/StartScreen'
import Main from './components/Main'
import Question from './components/Question'
import NextButton from './components/NextButton'
import Progress from './components/Progress'
import FinishScreen from './components/FinishScreen'

const initialState= {
  questions: [],
  status: "loading",   // status could be loading, error, ready, active , finished
  index: 0,
  answer: null,
  points: 0,
}

function reducer(state,action){
  switch(action.type){
    case "dataRecieved":{
      return {
        ...state,
        questions : action.payload,
        status: "ready",
      }
    }
    case "dataFailed":
      return {
        ...state, status :"error"
      }
    case "start":
      return {
        ...state , status : "active"
      }
    case "newAnswer":
      const question = state.questions.at(state.index)
      return {
        ...state, answer: action.payload, points : action.payload === question.correctOption ? state.points + question.points : state.points
      }
    case "nextQuestion":
      return {
        ...state , index : state.index + 1, answer: null
      }
      case "finished":
      return {
        ...state , status: "finished"
      }
    default:{
      throw new Error("Action Known")
    }
  }
}

export default function App(){
  const [{questions, status, index, answer, points}, dispatch] = useReducer(reducer, initialState)

  const numQuestions = questions.length
  const maxPossiblePoints = questions.reduce((prev, cur) => prev + cur.points, 0)

  useEffect(function(){
    fetch("http://localhost:8000/questions").then(res=>res.json()).then(data=>dispatch({type:"dataRecieved", payload: data})).catch(err=>dispatch({type:"dataFailed"}))
  },[])

  return <div className="app">
    <Header />
    <Main>
      {status === "loading" && <Loader/>}
      {status === "error" && <Error/>}
      {status === "ready" && <StartScreen numQuestions={numQuestions} dispatch={dispatch}/>}
      {status === "active" && <>
        <Progress index={index} numQuestions={numQuestions} points={points} maxPossiblePoints={maxPossiblePoints} answer={answer}/>
        <Question question={questions[index]} dispatch={dispatch} answer={answer}/>
        <NextButton dispatch={dispatch} answer={answer} index={index} numQuestions={numQuestions}/>
      </>
      }
      {status === "finished" && <FinishScreen points={points} maxPossiblePoints={maxPossiblePoints}/>}
    </Main>
  </div>
}
